query7="SELECT p.nom, fp.quantite FROM factures_produits fp LEFT JOIN produits p ON p.id_produit=fp.id_produit LEFT JOIN factures f on f.id_fact=fp.id_fact LEFT JOIN clients c on c.id_client=f.id_client WHERE raison_sociale='Bo Meuble' AND ville_c='Paris'"
query8="SELECT numero from factures WHERE date>'2018-07-01'"
query9="SELECT p.nom, SUM(fp.quantite) as 'Quantité commandée' FROM factures f LEFT JOIN factures_produits fp ON fp.id_fact=f.id_fact LEFT JOIN produits p ON p.id_produit=fp.id_produit GROUP BY p.nom"

queryst1="SELECT p.nom, spt.usine FROM produits p LEFT JOIN sites_produits sp ON sp.id_produit=p.id_produit LEFT JOIN sites_production spt ON spt.id_site=sp.id_site;"
queryst4="SELECT c.raison_sociale, c.ville_c, SUM(fp.quantite) as 'Quantité commandée' FROM factures f LEFT JOIN factures_produits fp ON fp.id_fact=f.id_fact LEFT JOIN clients c ON f.id_client=c.id_client GROUP BY c.id_client"